import { inject } from 'vue';
/**
 * Injection key for page language
 */
export const pageLangSymbol = Symbol(__VUEPRESS_DEV__ ? 'pageLang' : '');
/**
 * Returns the ref of the language of current page
 */
export const usePageLang = () => {
    const pageLang = inject(pageLangSymbol);
    if (!pageLang) {
        throw new Error('usePageLang() is called without provider.');
    }
    return pageLang;
};
/**
 * Resolve page language from page data
 *
 * It would be used as the `lang` attribute of `<html>` tag
 */
export const resolvePageLang = (pageData) => pageData.lang || 'en';
